sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
		if ( newValue == '') {
			return;
		}

		var selectedLesson = g_form.getValue('u_selected_lesson_sr');

		// Clear all of the choices from the What needed field choice list
		g_form.clearOptions('u_selected_lesson_sr');

		// If the the value of the Request type field is hr, add
		// two hr choices and other to the What needed field choice list
		if(newValue == 'math'){
			g_form.addOption('u_selected_lesson_sr','algebra','Algebra');
			g_form.addOption('u_selected_lesson_sr','calculus','Calculus');
			g_form.addOption('u_selected_lesson_sr','geometry','Geometry');
			g_form.addOption('u_selected_lesson_sr','other','Other');
		}
		// If the the value of the Request type field is facilities, add
		// two facilities choices and other to the What needed field
		// choice list
	if(newValue == 'english'){
			g_form.addOption('u_selected_lesson_sr','speech','Speech');
			g_form.addOption('u_selected_lesson_sr','poetry','Poetry');
			g_form.addOption('u_selected_lesson_sr','other','Other');
		}
		// If the the value of the Request type field is legal, add
		// two legal choices and other to the What needed field
		// choice list
		if(newValue == 'science'){
			g_form.addOption('u_selected_lesson_sr','biology','Biology');
			g_form.addOption('u_selected_lesson_sr','physics','Physics');
			g_form.addOption('u_selected_lesson_sr','other','Other');
		}

			if(newValue == 'your choice'){
		    g_form.addOption('u_selected_lesson_sr','algebra','Algebra');
			g_form.addOption('u_selected_lesson_sr','calculus','Calculus');
			g_form.addOption('u_selected_lesson_sr','geometry','Geometry');
			g_form.addOption('u_selected_lesson_sr','speech','Speech');
			g_form.addOption('u_selected_lesson_sr','poetry','Poetry');
			g_form.addOption('u_selected_lesson_sr','biology','Biology');
			g_form.addOption('u_selected_lesson_sr','physics','Physics');	
			g_form.addOption('u_selected_lesson_sr','other','Other');	


		}

		// If the form is loading and it is not a new record, set the u_what_needed value to the
		// value from the record before it was loaded
		if(isLoading && !g_form.isNewRecord()){
			g_form.setValue('u_selected_lesson_sr', selectedlesson);


		}
	}
